import {Chrono} from 'react-chrono'

import CourseTimelineCard from '../CourseTimelineCard'
import ProjectTimelineCard from '../ProjectTimelineCard'

import './index.css'

const TimelineView = props => {
  const {timelineItemsList} = this.props
  const courseList = timelineItemsList.filter(
    eachItem => eachItem.categoryId === 'COURSE',
  )
  const projectList = timelineItemsList.filter(
    eachItem => eachItem.categoryId === 'PROJECT',
  )

  return (
    <div className="app-container">
      <div className="responsive-container">
        <h1 className="heading">MY JOURNEY OF</h1>
        <h1 className="main-heading">CCBP 4.0</h1>
        <div className="chrono-container">
          <Chrono items={courseList} mode="VERTICAL_ALTERNATING">
            <ul className="course-list">
              {courseList.map(eachCourse => (
                <CourseTimelineCard
                  key={eachCourse.id}
                  courseDetails={eachCourse}
                />
              ))}
            </ul>
          </Chrono>
          <Chrono items={projectList} mode="VERTICAL_ALTERNATING">
            <ul className="project-list">
              {projectList.map(eachProject => (
                <ProjectTimelineCard
                  key={eachProject.id}
                  projectDetails={eachProject}
                />
              ))}
            </ul>
          </Chrono>
        </div>
      </div>
    </div>
  )
}

export default TimelineView
